import { createSlice } from "@reduxjs/toolkit";

const initialCartState = {
  items: [],
  totalQuantity: 0,
  cartState: 0,
};

const cartSlice = createSlice({
  name: "cart",
  initialState: initialCartState,
  reducers: {
    addItemToCart(state, action) {
      const payloadItem = action.payload;
      const existingItem = state.items.find(
        (item) => item.id === payloadItem.id
      );

      if (!existingItem) {
        state.items.push({
          id: payloadItem.id,
          price: payloadItem.price,
          quantity: 1,
          totalPrice: payloadItem.price,
          title: payloadItem.title,
        });

      } else {
          existingItem.totalQuantity++
          existingItem.totalPrice = existingItem.totalPrice + payloadItem.price
      }
    },

    removeItemFromCart(state, action) {
      if (state.totalQuantity === 0) {
        state.items.filter((item) => item !== action.payload);
        return;
      }
      state.totalQuantity--;

      state.totalQuantity--;
    },
  },
});

export const cartActions = cartSlice.actions;

export default cartSlice;
