import { createSlice } from "@reduxjs/toolkit";

const initialCartState = { counter: 0, cartState: 0 };

const cartSlice = createSlice({
  name: "cart",
  initialState: initialCartState,
  reducers: {
    increment(state) {
      state.counter++;
    },

    decrement(state) {
      if (state.counter === 0) {
        return;
      }

      state.counter--;
    },

    addToCart(state) {
      state.cartState = state.counter;
    },
  },
});
